import { useState } from 'react'

const StatisticsLine = (props) => {
	return (

			<tr>
				<td>{props.text}</td>
				<td>{props.value}</td>
			</tr>

		)
}

const Statistics = (props) => {
	if (props.allClicks === 0 ) {
		return (
			<p>No feedback given</p>
		)
	}
		return (
			<table>
				<tbody>
				<StatisticsLine text="good" value={props.good} />
				<StatisticsLine text="neutral" value={props.neutral} />
				<StatisticsLine text="bad" value={props.bad} />
				<StatisticsLine text="all" value={props.good + props.neutral + props.bad} />
				<StatisticsLine text="average" value={(props.good - props.bad) / (props.good + props.neutral + props.bad)} />
				<StatisticsLine text="positive" value={props.good / (props.good + props.neutral + props.bad) * 100 + " %"}/>
				</tbody>	
			</table>
	)
}

const App = () => {
	// save clicks of each button to its own state
	const [good, setGood] = useState(0)
	const [neutral, setNeutral] = useState(0)
	const [bad, setBad] = useState(0)
	const [allClicks, setAll] = useState(0)



	const handleNeutral = () => {
		setNeutral(neutral + 1)
		setAll(allClicks + 1)
	}

	const handleBad = () => {
		setBad(bad + 1)
		setAll(allClicks + 1)

	}

	return (
		<div>
			<h1>give feedback</h1>
			<button onClick={handleGood}>good</button>
			<button onClick={handleNeutral}>neutral</button>
			<button onClick={handleBad}>bad</button>
			<h1>statistics</h1>
			<Statistics good={good} neutral={neutral} bad={bad} all={good + neutral + bad} allClicks={allClicks} ></Statistics>
		</div>
  )
}

export default App
