import axios from 'axios';
import React, { useEffect, useState } from 'react';

const App = () => {
	const [countries, setCountries] = useState([])
	const [filter, setFilter] = useState('');

	// console.log('%cApp.js line:9 filter', 'color: #007acc;', filter);
	return (
		<div>
			<Fetch setCountries={setCountries}></Fetch>
			<Form setFilter={setFilter}></Form>
			<Countries countries={countries} filter={filter}></Countries>

		</div>
	)
}

const Fetch = ({ setCountries }) => {

	useEffect(() => {
		axios
			.get('https://restcountries.com/v3.1/all')
			.then(response => {
				setCountries(response.data)
			})
	}, [])

	return (
		<div>

		</div>
	)
}

const Form = ({ setFilter }) => {

	const handleFilterChange = (event) => {
		setFilter(event.target.value)
	};

	return (
		<div>
			<form>
				find countries:
				<input onChange={handleFilterChange}></input>
			</form>
		</div>
	);
};

const Countries = ({ countries, filter }) => {
	const filtered = countries.filter((country) =>
		country.name.common.toLowerCase().includes(filter.toLowerCase()))

	const filteredCount = filtered.length

	if (filteredCount > 10)
		return <p>Too many matches, specify another filter</p>
	else if (filteredCount < 10 && filteredCount > 1) //! Can be moved to own component
		return (
			<div>
				{filtered.map(country =>
					<p>{country.name.common}</p>
				)}
			</div>
		)

	else if (filteredCount == 1) {
		// const languages = Object.values(filtered[0].languages)
		return (
			<Country country={filtered[0]}></Country>
			// <div>
			// 	<h1>{filtered[0].name.common}</h1>
			// 	<p>capital: {filtered[0].capital}</p>
			// 	<p>area: {filtered[0].area}</p>
			// 	<h2>languages</h2>
			// 	{languages.map(language =>
			// 		<li>{language}</li>)}
			// </div>
		)
	}

	else
		return <p>no matches</p>
}


const Country = ({ filtered }) => {
	const languages = Object.values(filtered[0].languages)
	return (
		<div>
			<h1>{filtered[0].name.common}</h1>
			<p>capital: {filtered[0].capital}</p>
			<p>area: {filtered[0].area}</p>
			<h2>languages</h2>
			{languages.map(language =>
				<li>{language}</li>)}
		</div>
	)
}


export default App
