import React from 'react'

// const Content = ( {parts} ) => {
// 	return (
// 		parts.map(part => <p key={part.id}>{part.name} {part.exercises}</p>)
// 		// <Part {part.name} {part.exercise}></Part>
// 	)
// }

const Part = (props) => {
	return (
		<p>{props.part} {props.exercises}</p>
	)
}

const Total = (props) => {
	const total = props.parts[0].exercises + props.parts[1].exercises + props.parts[2].exercises
	return (
		<div>
			<p>Number of exercises {total}</p>
		</div>
	)
}

const Content = (props) => {
	return (
		<div>
			<Part part={props.parts[0].name} exercises={props.parts[0].exercises}></Part>
			<Part part={props.parts[1].name} exercises={props.parts[1].exercises}></Part>
			<Part part={props.parts[2].name} exercises={props.parts[2].exercises}></Part>
		</div>
	)
}

const Course = ( {course} ) => {

	return (
		<div>
			<Header name={course.name}></Header>
			<Content parts={course.parts}></Content>
			{/* <strong>total of exercises {total} exercises</strong> */}
		</div>
	)
}

const App = () => {
	const course = {
	  id: 1,
	  name: 'Half Stack application development',
	  parts: [
		{
		  name: 'Fundamentals of React',
		  exercises: 10,
		  id: 1
		},
		{
		  name: 'Using props to pass data',
		  exercises: 7,
		  id: 2
		},
		{
		  name: 'State of a component',
		  exercises: 14,
		  id: 3
		}
	  ]
	}

	return (
		<Course course={course} />
	)
  }

  export default App
