import { useState } from 'react'
import React from 'react'

const Person = ({person}) => {
	console.log(person)
	return (
		<p>{person.name} {person.number}</p>
	)
}

const App = () => {
  const [persons, setPersons] = useState([
    {	name: 'Arto Hellas', number: '040-1234567', id: 0}
  ]) 

  const [newName, setNewName] = useState('')
  const [newNumber, setNewNumber] = useState('')
  const [filter, setFilter] = useState('')

  const addPerson = (event) => {
	event.preventDefault()

	const result = persons.filter(person => person.name === newName)
	if (result.length)
		return (window.alert(`${newName} is already added to phonebook`))

		const personObject = {
			name: newName,
			number: newNumber,
			id: persons.length + 1
		}
		setPersons(persons.concat(personObject))

}

const handleFilterChange = (event) => {
  setNewNumber(event.target.value)
}

  const handleNameChange = (event) => {
	setNewName(event.target.value)
  }

  const handleNumberChange = (event) => {
	setNewNumber(event.target.value)
  }


  return (
    <div>
      <h2>Phonebook</h2>
	  <form>
		<div>filter shown with <input value={filter} onChange={handleFilterChange}></input>
		</div>
	  </form>

      <h2>add new</h2>
	<form onSubmit={addPerson}>
		<div>name: <input value={newName} onChange={handleNameChange} /></div>
		<div>number: <input value={newNumber} onChange={handleNumberChange}/></div>

		<div><button type="submit">add</button></div>
	</form>
      <h2>Numbers</h2>
	  {persons.map(person => 
		<Person key={person.id} person={person} phone={person.number} />
	  )}
    </div>
  )

}
export default App
