import axios from 'axios'
import React from 'react'
const baseUrl = "http://localhost:3001/persons"

const getAll = () => {
  return axios.get(baseUrl)
}

const create = newObject => {
	return axios.post(baseUrl, newObject)
}

export default {
	getAll: getAll,
	create: create,
	update: update
}
