import React from 'react'
import { useState } from 'react'

export default function App() {

	const [value, setValue] = useState(0);
	function decreaseValue() {
		setValue(oldValue => oldValue - 1)
	}
  return (
	  <div>
		<button onClick={decreaseValue} >-</button>
		<span>{value}</span>
		<button>+</button>
	</div>
  )
}
