import { useEffect, useState } from 'react'
// import axios from 'axios'
import React from 'react'
import personService from './services/persons.js'

const App = () => {
	const [persons, setPersons] = useState([])
	const [newName, setNewName] = useState('')
	const [newNumber, setNewNumber] = useState('')
	const [filter, setFilter] = useState("")

	const deleteUser = id => {
		personService.deleteUser(id)
	}
	useEffect(() => {
		noteService
		  .getAll()
		  .then(initialNotes => {
			setNotes(initialNotes)
		  })
	  }, [])

	  const addNote = (event) => {
		event.preventDefault()
		const noteObject = {
		  content: newNote,
		  date: new Date().toISOString(),
		  important: Math.random() > 0.5,
		  id: notes.length + 1,
		}

		noteService
		  .create(noteObject)
		  .then(returnedNote => {
			setNotes(notes.concat(returnedNote))
			setNewNote('')
		  })
	  }
	useEffect(() => {
		personService
			.getAll()
			.then(perons => {
				setPersons(perons)
			})
	}, [])

	return (
		<div>
			<h2>Phonebook</h2>
			<Filter filter={filter} setFilter={setFilter} />
			<h3>add new</h3>
			<Form
				persons={persons}
				newName={newName}
				newNumber={newNumber}
				setNewName={setNewName}
				setNewNumber={setNewNumber}
			/>

			<h3>Numbers</h3>
			<Persons
				persons={persons}
				filter={filter}
				deleteUser={deleteUser}></Persons>
		</div>
	)
}

const Filter = ({ filter, setFilter }) => {
	const handleFilterChange = (event) => {
		setFilter(event.target.value)
	}

	return (
		<form>
			<div>
				filter shown with: <input value={filter} onChange={handleFilterChange}></input>
			</div>
		</form>
	)
}

const Form = ({
	persons,
	newName,
	newNumber,
	setNewName,
	setNewNumber }) => {

	const handleNameChange = (event) => {
		setNewName(event.target.value)
	}

	const handleNumberChange = (event) => {
		setNewNumber(event.target.value)
	}

	const addPerson = (event) => {
		event.preventDefault()
		const result = persons.filter(person => person.name === newName)
		if (result.length)
			return (window.alert(`${newName} is already added to phonebook`))

		const personObject = {
			name: newName,
			number: newNumber,
		}

		personService
			.create(personObject)
	}
	return (
		<form onSubmit={addPerson}>
			<div>
				name: <input value={newName} onChange={handleNameChange} />
			</div>
			<div>
				number: <input value={newNumber} onChange={handleNumberChange} />
			</div>
			<div>
				<button type="submit">add</button>
			</div>
		</form>
	)
}

const Persons = ({ persons, filter, deleteUser }) => {
	const filteredPersons = persons.filter((person) =>
		person.name.toLowerCase().includes(filter.toLowerCase()))

	return (
		<>
			{filteredPersons.map(person =>
				<p key={person.id}>
					{person.name} {person.number}
					<button onClick={() => deleteUser(person.id)}>
						delete
					</button>
				</p>
			)}
		</>
	)
}

export default App
