import { useState } from 'react'
import React from 'react'

const App = () => {
	const [persons, setPersons] = useState([
		{ name: 'Arto Hellas', number: '040-123456', id: 1 },
		{ name: 'Ada Lovelace', number: '39-44-5323523', id: 2 },
		{ name: 'Dan Abramov', number: '12-43-234345', id: 3 },
		{ name: 'Mary Poppendieck', number: '39-23-6423122', id: 4 }
	])

	const [newName, setNewName] = useState('')
	const [newNumber, setNewNumber] = useState('')
	const [filter, setFilter] = useState("")

	return (
		<div>
			<h2>Phonebook</h2>
			<Filter setFilter={setFilter} persons={persons} filter={filter}/>
			<h3>add new</h3>
			<Form
			persons={persons}
			newName={newName}
			newNumber={newNumber}
			setPersons={setPersons}
			setFilter={setFilter}
			setNewName={setNewName}
			setNewNumber={setNewNumber}
			/>

			<h3>Numbers</h3>
			<Persons persons={persons}></Persons>

			{/* {personsToShow.map(person =>
				<Person key={person.id} person={person} phone={person.number} />
			)} */}



			<h2>add new</h2>
			<form onSubmit={addPerson}>
				<div>
					name: <input value={newName} onChange={handleNameChange} />
				</div>
				<div>
					number: <input value={newNumber} onChange={handleNumberChange} />
				</div>
				<div>
					<button type="submit">add</button>
				</div>
			</form>
			<h2>Numbers</h2>
			{personsToShow.map(person =>
				<Person key={person.id} person={person} phone={person.number} />
			)} */}
		</div>
	)
}

const Filter = ({persons, filter, setFilter}) => {
	const handleFilterChange = (event) => {
		setFilter(event.target.value)
	}
	const personsToShow = persons.filter((person) =>
	person.name.toLowerCase().includes(filter.toLowerCase()))

	return (
		<form>
			<div>
				filter shown with: <input value={filter} onChange={handleFilterChange}></input>
			</div>
		</form>
		)
}

const Form = ({
	persons,
	newName,
	newNumber,
	setPersons,
	setFilter,
	setNewName,
	setNewNumber}
	) => {

	const handleNameChange = (event) => {
		setNewName(event.target.value)
	}

	const handleNumberChange = (event) => {
		setNewNumber(event.target.value)
	}
	const addPerson = (event) => {
		event.preventDefault()

		const result = persons.filter(person => person.name === newName)
		if (result.length)
			return (window.alert(`${newName} is already added to phonebook`))

		const personObject = {
			name: newName,
			number: newNumber,
			id: persons.length + 1
		}
		setPersons(persons.concat(personObject))
	}
	return (
		<form onSubmit={addPerson}>
		<div>
			name: <input value={newName} onChange={handleNameChange} />
		</div>
		<div>
			number: <input value={newNumber} onChange={handleNumberChange} />
		</div>
		<div>
			<button type="submit">add</button>
		</div>
	</form>
	)
}

const Persons = ({ person }) => {
	return (
		<p>{person.name} {person.number}</p>
	)
}

export default App
