import { useState } from 'react'
import React from 'react'

const App = () => {
	const [persons, setPersons] = useState([
		{ name: 'Arto Hellas', number: '040-123456', id: 1 },
		{ name: 'Ada Lovelace', number: '39-44-5323523', id: 2 },
		{ name: 'Dan Abramov', number: '12-43-234345', id: 3 },
		{ name: 'Mary Poppendieck', number: '39-23-6423122', id: 4 }
	])

	const [newName, setNewName] = useState('')
	const [newNumber, setNewNumber] = useState('')
	const [filter, setFilter] = useState("")

	const handleFilterChange = (event) => {
		setFilter(event.target.value)
	}

	const handleNameChange = (event) => {
		setNewName(event.target.value)
	}

	const handleNumberChange = (event) => {
		setNewNumber(event.target.value)
	}

	return (
		<div>
			<h2>Phonebook</h2>
			{/* <Filter ></Filter> */}
			<h3>add new</h3>
			<Form newName={newName} persons={persons}></Form>
			<h3>Numbers</h3>
			{/* <Person></Person> */}

			{/* {personsToShow.map(person =>
				<Person key={person.id} person={person} phone={person.number} />
			)} */}

			{/* <h2>Phonebook</h2>
			<form>
				<div>filter shown with: <input value={filter} onChange={handleFilterChange}></input>
				</div>
			</form>

			<h2>add new</h2>
			<form onSubmit={addPerson}>
				<div>
					name: <input value={newName} onChange={handleNameChange} />
				</div>
				<div>
					number: <input value={newNumber} onChange={handleNumberChange} />
				</div>
				<div>
					<button type="submit">add</button>
				</div>
			</form>
			<h2>Numbers</h2>
			{personsToShow.map(person =>
				<Person key={person.id} person={person} phone={person.number} />
			)} */}
		</div>
	)
}

const Form = ({newName}, {persons}, {newNumber}) => {

	const addPerson = (event) => {
		event.preventDefault()

		const result = persons.filter(person => person.name === newName)
		if (result.length)
			return (window.alert(`${newName} is already added to phonebook`))

		const personObject = {
			name: newName,
			number: newNumber,
			id: persons.length + 1
		}
		setPersons(persons.concat(personObject))
	}
	return (
		<form onSubmit={addPerson}>
		<div>
			name: <input value={newName} onChange={handleNameChange} />
		</div>
		<div>
			number: <input value={newNumber} onChange={handleNumberChange} />
		</div>
		<div>
			<button type="submit">add</button>
		</div>
	</form>
	)
}
const Person = ({ person }) => {
	return (
		<p>{person.name} {person.number}</p>
	)
}

