import { useEffect, useState } from 'react'
// import axios from 'axios'
import React from 'react'
import personService from './services/persons.js'

const App = () => {
	const [persons, setPersons] = useState([])
	const [newName, setNewName] = useState('')
	const [newNumber, setNewNumber] = useState('')
	const [filter, setFilter] = useState("")

	const deleteUser = id => {
		// console.log(persons.find(p => p.id === id))
		const person = persons.find(n => n.id === id)
		// if (window.confirm("Delete" `${person.name}`))
		// if (window.confirm("Delete" `${person.name}`))
		personService
			.deleteUser(id)
		// const person = persons.find(n => n.id === id)
		const deletableUser = person.id
		// console.log(deletableUser)

		// setPersons(persons.map(person => person.id !== deletableUser)) //! Use filter method NEVER MUTATE
			// .then(response => {
				// console.log(response)
	}

	useEffect(() => {
		personService
			.getAll()
			.then(persons => {
				setPersons(persons)
			})
	}, [])

	return (
		<div>
			<h2>Phonebook</h2>
			<Filter filter={filter} setFilter={setFilter} />
			<h3>add new</h3>
			<Form
				persons={persons}
				newName={newName}
				newNumber={newNumber}
				setNewName={setNewName}
				setPersons={setPersons}
				setNewNumber={setNewNumber}
			/>

			<h3>Numbers</h3>
			<Persons
				persons={persons}
				filter={filter}
				deleteUser={deleteUser}></Persons>
		</div>
	)
}

const Filter = ({ filter, setFilter }) => {
	const handleFilterChange = (event) => {
		setFilter(event.target.value)
	}

	return (
		<form>
			<div>
				filter shown with: <input value={filter} onChange={handleFilterChange}></input>
			</div>
		</form>
	)
}

const Form = ({
	persons,
	newName,
	newNumber,
	setPersons,
	setNewName,
	setNewNumber }) => {

	const handleNameChange = event => {
		setNewName(event.target.value)
	}

	const handleNumberChange = event => {
		setNewNumber(event.target.value)
	}

	const addPerson = event => {
		event.preventDefault()
		const result = persons.filter(person => person.name === newName)
		if (result.length)
			return (window.alert(`${newName} is already added to phonebook`))

		const personObject = {
			name: newName,
			number: newNumber,
		}

		personService
			.create(personObject)
			.then(response => setPersons(persons.concat(response.data)))

	}
	return (
		<form onSubmit={addPerson}>
			<div>
				name: <input value={newName} onChange={handleNameChange} />
			</div>
			<div>
				number: <input value={newNumber} onChange={handleNumberChange} />
			</div>
			<div>
				<button type="submit">add</button>
			</div>
		</form>
	)
}

const Persons = ({ persons, filter, deleteUser }) => {
	const filteredPersons = persons.filter((person) =>
		person.name.toLowerCase().includes(filter.toLowerCase()))

	return (
		<>
			{filteredPersons.map(person =>
				<p key={person.id}>
					{person.name} {person.number}
					<button onClick={() => deleteUser(person.id)}>
						delete
					</button>
				</p>
			)}
		</>
	)
}

export default App
